import React, { useState } from 'react';
import './_OrderPopup.scss';

function OrderPopup(props) {
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleSubmit = () => {
        // Показуємо повідомлення про успішне замовлення
        setIsSubmitted(true);

        // Через 5 секунд зникаємо повідомлення
        setTimeout(() => {
            setIsSubmitted(false);
        }, 5000);
    };

    return (
        <div >
            <div className="order-popup__container">
                {isSubmitted ? (
                    <>
                        <div className="order-popup__icon"></div>
                        <h1 className="order-popup__title">Дякую за замовлення</h1>
                        <p className="order-popup__subtitle">
                            Наш менеджер зв'яжеться з вами найближчим часом
                        </p>
                    </>
                ) : (
                    <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
                        <input type="text" placeholder="Ім'я" required />
                        <input type="text" placeholder="Прізвище" required />
                        <input type="tel" placeholder="Номер телефону" required />
                        <button type="submit">Замовити</button>
                    </form>
                )}
            </div>
        </div>
    );
}

export default OrderPopup;
