import React, { useState } from 'react';
import './_OrderPopup.scss';

function OrderPopup(props) {
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [isFormVisible, setIsFormVisible] = useState(true);

    const handleSubmit = () => {
        setIsSubmitted(true);
        setIsFormVisible(false);
    };

    return (
        <div className={`order-popup ${props.isOrderDone && 'order-popup_opened'}`}>
            <div className="order-popup__container">
                {isSubmitted ? (
                    <>
                        <div className="order-popup__icon"></div>
                        <h1 className="order-popup__title">Дякую за замовлення</h1>
                        <p className="order-popup__subtitle">Наш менеджер зв'яжеться з вами найближчим часом</p>
                    </>
                ) : (
                    isFormVisible && (
                        <form onSubmit={handleSubmit}>
                            <input type="text" placeholder="Ім'я" required />
                            <input type="text" placeholder="Прізвище" required />
                            <input type="tel" placeholder="Номер телефону" required />
                            <button type="submit">Замовити</button>
                        </form>
                    )
                )}
            </div>
        </div>
    );
}

export default OrderPopup;
