import React, { useState } from 'react';
import './_OrderPopup.scss';

function OrderPopup(props) {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phoneNumber: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Ваша логіка для обробки замовлення тут

        // Показуємо повідомлення про успішне замовлення
        setIsSubmitted(true);

        // Через 5 секунд зникаємо повідомлення
        setTimeout(() => {
            setIsSubmitted(false);
            // Очищаємо дані форми
            setFormData({ firstName: '', lastName: '', phoneNumber: '' });
        }, 5000);
    };

    return (
        <div className={`order-popup ${isSubmitted && 'order-popup_opened'}`}>
            <div className="order-popup__container">
                {isSubmitted ? (
                    <>
                        <div className="order-popup__icon"></div>
                        <h1 className="order-popup__title">Дякую за замовлення</h1>
                        <p className="order-popup__subtitle">
                            Наш менеджер зв'яжеться з вами найближчим часом
                        </p>
                    </>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={handleChange}
                            placeholder="Ім'я"
                            required
                        />
                        <input
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                            placeholder="Прізвище"
                            required
                        />
                        <input
                            type="tel"
                            name="phoneNumber"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                            placeholder="Номер телефону"
                            required
                        />
                        <button type="submit">Замовити</button>
                    </form>
                )}
            </div>
        </div>
    );
}

export default OrderPopup;
